export default {
  name: "ClientUserPassword",
  data() {
    return {
      form: {},
      user: localStorage.getItem("user") ? JSON.parse(localStorage.getItem("user")) : {},
      rules: {
        password: [{
          required: true,
          message: '请输入原密码',
          trigger: 'blur'
        }, {
          min: 3,
          message: '长度不少于3位',
          trigger: 'blur'
        }],
        newPassword: [{
          required: true,
          message: '请输入新密码',
          trigger: 'blur'
        }, {
          min: 3,
          message: '长度不少于3位',
          trigger: 'blur'
        }],
        confirmPassword: [{
          required: true,
          message: '请输入密码',
          trigger: 'blur'
        }, {
          min: 3,
          message: '长度不少于3位',
          trigger: 'blur'
        }]
      }
    };
  },
  created() {
    this.form.username = this.user.username;
  },
  methods: {
    save() {
      this.$refs.pass.validate(valid => {
        if (valid) {
          if (this.form.newPassword !== this.form.confirmPassword) {
            this.$message.error("2次输入的新密码不相同");
            return false;
          }
          this.request.post("http://localhost:9090/user/password", this.form).then(res => {
            if (res.code === '200') {
              this.$message.success("修改成功");
              this.$store.commit("logout");
            } else {
              this.$message.error(res.msg);
            }
          });
        }
      });
    }
  }
};
