import request from "../../utils/request";
export default {
  name: "CommentManage",
  data() {
    return {
      tableData: [],
      time: '',
      name: "",
      total: 0,
      pageNum: 1,
      pageSize: 2,
      multipleSelection: [],
      // 多选框，选定时的内容
      dialogFormVisible: false,
      // 增加功能对话框默认不展示
      headerBg: 'headerBg',
      selectedDate: null
    };
  },
  methods: {
    load() {
      this.request.get("http://localhost:9090/comment/page", {
        params: {
          pageNum: this.pageNum,
          pageSize: this.pageSize
        }
      }).then(res => {
        console.log(res);
        this.tableData = res.records; // records 请看控制台的输出
        this.total = res.total;
        console.log(res);
      });
    },
    delBatch() {
      let ids = this.multipleSelection.map(v => v.id);
      console.log(ids);
      this.request.post("http://localhost:9090/show/del/batch", ids).then(res => {
        if (res) {
          this.$message.success("删除成功");
          this.load();
        } else {
          this.$message.error("删除失败");
        }
      });
    },
    handleDel(id) {
      this.request.delete("http://localhost:9090/comment/" + id).then(res => {
        if (res) {
          this.$message.success("删除成功");
          this.load();
        } else {
          this.$message.error("删除失败");
        }
      });
    },
    reset() {
      this.name = "";
      this.load();
    },
    handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    handleSizeChange(pageSize) {
      this.pageSize = pageSize;
      this.load();
    },
    handleCurrentChange(pageNum) {
      this.pageNum = pageNum;
      this.load();
    }
  },
  created() {
    this.load();
    this.form.begainTime = new Date(); // 设置默认时间为当前时间
  },

  mounted() {}
};
